plot_estimates_pngs(data = cccharts::glacial, geom = "bar", ask = FALSE)
plot_estimates_pngs(data = cccharts::precipitation, x = "Ecoprovince", facet = "Season", geom = "bar", ci = FALSE, height = 500L, width = 500L, ask = FALSE)
